				type: 'pie',
				radius: ['82',  '102'],
				center: ['32%', '50%'],
				itemStyle: {
					color: function (params: any) {
						return colorList[params.dataIndex];
					},
				},
				label: { show: false },
				labelLine: { show: false },
				data: data,
			},
		],
	};
	myChart.setOption(option);
  state.myCharts.push(myChart)
}
