//监听容器大小
const initEchartsResize = () => {
  nextTick(() => {
    state.myCharts.forEach((item,index) => {
      setTimeout(() => {
        item.resize();
      },index*1000)
    })
  })
}

onMounted(()=>{
  window.addEventListener('resize',initEchartsResize)
  nextTick(() => {
		setTimeout(() => {
			initLineChart();
		}, 500);
		setTimeout(() => {
			initPieChart();
		}, 700);
		setTimeout(() => {
			initBarChart();
		}, 1000);
	});
})


</script>

<style scoped lang="scss">
.home-container {
